import React from 'react';

import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import './pages.css'

import {fetchProducts} from './../../store/actions';

// import Auxilary from '../hoc/Auxilary/Auxilary.js'




class Products extends React.Component{

  state = {
   products: [],
  }

  componentDidMount(){
    this.setState({user: this.props.data.user}, () => {
        this.changePage();
        this.fetchProducts();
    })
  }

  fetchProducts(){
      if(this.state.user){
          if(this.state.user.token){
              this.setState({loading: true}, ()=>{
                this.props.fetchProducts(this.state.user.token).then(()=>{
                    this.setState({loading: false, products: this.props.data.products})
                })
              })  
          }
      }
  }

  componentDidUpdate(props){
    if(this.props !== props){
      this.setState({user: this.props.data.user})
    }
  }

  changePage = () => {
      if(!this.state.user || this.state.user.error){
          this.props.history.push('/')
      }
  }

  renderProducts(){
    if(this.state.loading){
      return <div className="col text-center mt-4 mb-4"><i className="fa fa-spinner fa-spin"></i></div>
    }else{
      if(this.state.products.length === 0){
        return <div className="col">There are no products associated with your account.</div>
      }else{
        return this.state.products.map(product => {
          return(
            <div key={product.id} className="col-md-3 mb-4">
                <div className="product-tile">
                    <h4>{product.name} </h4>
                    $ {product.price}
                </div>
            </div>
          )
        })
      }
    }
  }

  render(){
      console.log(this.state)
    return(
        <div className="container mt-4 mb-4">
            <h2 className="mb-4 mt-4"> Products </h2>
            <div className="row">
                {this.renderProducts()}
            </div>     
        </div>
    );
  }

};


const mapStateToProps = state => {
	return{
        data: state.Data
	}
};


export default connect(mapStateToProps, {fetchProducts})(Products);
