import { combineReducers } from 'redux';

const user = (state = null, action) => {
  console.log(state)
  switch (action.type) {
    case "FETCH_USER":
      return action.payload || false;
    default:
      return state;
  }
}

const INITIAL_USER_STATE = {
  user: null
}

const userData = (state=INITIAL_USER_STATE, action) => {
  switch(action.type){
    case 'FETCH_USER':
      return {...state,
        user: action.payload,
      }
    case 'LOGOUT':
      return {...state,
        user: null
      }
    default:
      return state;
  }
};


export default combineReducers({
  user: userData.user,
})
