/** 
 * This component consists of underline functionalities:
 * User Login form asks for email and password, and both fields are sent to backend server for authentication
 * User details are sent back from server to frontend with jwt token if credentials are valid otherwise error is shown
 * User is redirected to dashboard component on authentication
 * Link to PID Digitization Demo takes user to Guest Demo Form
 */

import React from 'react';

import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import Auxilary from './../../hoc/Auxilary/Auxilary';
import './Forms.css';
import {addProduct} from './../../store/actions';

class AddProduct extends React.Component{
    state = {
      name: "",
      price: "",
      description: "",
      product: null,
      user: null,
      loading: false
	}

  componentDidMount(){
    this.setState({user: this.props.data.user}, this.changePage);
  }

  componentDidUpdate(props){
    if(this.props !== props){
      this.setState({user: this.props.data.user})
    }
  }

  addProductDataHandler = (event) =>{

    event.preventDefault();

    if(this.state.user){
        if(this.state.user.token){
            this.setState({loading: true}, ()=>{
                let formData = new FormData();
                formData.append('name', this.state.name);
                formData.append('price', this.state.price);
                formData.append('description', this.state.description);
                formData.append('image_url', 'url')
                this.props.addProduct(formData, this.state.user.token).then(
                 this.changeState 
                );
            })
        }    
    }
  }

  changeState = () => {
     this.setState({product: this.props.data.new_product, loading: false}, () => {
         if(this.state.product){
             if(!this.state.product.error){
                 this.props.history.push('/products')
             }
         }
     });
  }




  changePage = () => {
      if(!this.state.user || this.state.user.error){
          this.props.history.push('/')
      }
  }

  renderButton(){
    if(this.state.loading){
      return <button type="submit" className="btn btn-info float-right" disabled> Add <i className="fa fa-spinner fa-spin"></i> </button>;
    }
    return <button type="submit" className="btn btn-info float-right" > Add </button>;
  }


	render(){
      return(
        <Auxilary>
                <div className="container mb-8 mt-8 auth-form">
                    <div className="row">
                      <div className="col-md-4 offset-md-4">

                        <h2 className="mb-4 text-center"><b> Add Product </b></h2>

                        { (this.state.product) ?
                          (this.state.product.error)?
                            <div className="alert alert-danger text-center" role="alert">
                                {this.state.product.error}
                            </div>
                            :
                            null
                          :null
                        }

                        <form onSubmit={(event) => this.addProductDataHandler(event)}>

                          <div className="form-group">
                            <input
                              type='text'
                              className={(this.state.name!=='')?"form-control filled": "form-control"}
                              value={this.state.name}
                              onChange={(event)=>this.setState({name: event.target.value})}
                              required
                            />
                            <label>Name</label>                            
                          </div>

                          <div className="form-group">
                            <input
                              type='number'
                              step="0.01"
                              className={(this.state.price!=='')?"form-control filled": "form-control"}
                              value={this.state.price}
                              onChange={(event)=>this.setState({price: event.target.value})}
                              required
                            />
                            <label>Price</label>                            
                          </div>

                          <div className="form-group">
                            <textarea
                              className={(this.state.description!=='')?"form-control filled": "form-control"}
                              value={this.state.description}
                              onChange={(event)=>this.setState({description: event.target.value})}
                              required
                            />
                            <label>Description</label>                            
                          </div>

                          <div className="form-group forgot clearfix">
                              {this.renderButton()}
                          </div>


                        </form>
                      </div>                  
                    </div>
                </div>
        </Auxilary>
			);
	}
}


const mapStateToProps = state => {
	return{
        data: state.Data
	}
};

export default connect(mapStateToProps, {addProduct})(AddProduct);
