import React from 'react';

import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import {fetchProducts} from './../../store/actions';

// import Auxilary from '../hoc/Auxilary/Auxilary.js'




class Products extends React.Component{

  state = {
   products: [],
  }

  componentDidMount(){
    this.setState({user: this.props.data.user}, () => {
        this.changePage();
        this.fetchProducts();
    })
  }

  fetchProducts(){
      if(this.state.user){
          if(this.state.user.token){
              this.setState({loading: true}, ()=>{
                this.props.fetchProducts(this.state.user.token).then(()=>{
                    this.setState({loading: false, products: this.props.data.products})
                })
              })  
          }
      }
  }

  componentDidUpdate(props){
    if(this.props !== props){
      this.setState({user: this.props.data.user})
    }
  }

  changePage = () => {
      if(!this.state.user || this.state.user.error){
          console.log("Test")
          this.props.history.push('/')
      }
  }

  renderProducts(){
    if(this.state.loading){
      return <i className="fa fa-spinner fa-spin"></i>
    }else{
      if(this.state.products.length === 0){
        return <p>There are no products associated with your account.</p>
      }else{
        return this.state.products.map(product => {
          return(
            <div key={product.id}>{product.name}</div>
          )
        })
      }
    }
  }

  render(){
      console.log(this.state)
    return(
        <div className="container mt-4 mb-4">
            {this.renderProducts()}
        </div>
    );
  }

};


const mapStateToProps = state => {
	return{
        data: state.Data
	}
};


export default connect(mapStateToProps, {fetchProducts})(Products);
