/** 
 * This component consists of underline functionalities:
 * User Login form asks for email and password, and both fields are sent to backend server for authentication
 * User details are sent back from server to frontend with jwt token if credentials are valid otherwise error is shown
 * User is redirected to dashboard component on authentication
 * Link to PID Digitization Demo takes user to Guest Demo Form
 */

import React from 'react';

import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import Auxilary from './../../hoc/Auxilary/Auxilary';
import './Forms.css';
import {loginUser} from './../../store/actions';

class Login extends React.Component{
    state = {
      email: "",
      password: "",
      errorMessage: null,
      isSignedIn: false,
      loading: false
	}

  componentDidMount(){
    this.setState({user: this.props.user, errorMessage: null}, this.changePage);
  }

  loginDataHandler = (event) =>{

    event.preventDefault();

    if(this.state.email!=='' && this.state.password!==''){
       this.setState({loading: true}, ()=>{
         this.props.loginUser(this.state.email, this.state.password).then(
          this.changeState 
        );
       })
    }
  }

  changeState = () => {
     this.setState({isSignedIn: this.props.user.isSignedIn, errorMessage: this.props.user.errorMessage, loading: false}, this.changePage );
  }


  changePage = () => {
    if(this.state.isSignedIn){
      this.props.history.push('/dashboard');
    }
  }

  renderButton(){
    if(this.state.loading){
      return <button type="submit" className="btn btn-info float-right" disabled> Sign in <i className="fa fa-spinner fa-spin"></i> </button>;
    }
    return <button type="submit" className="btn btn-info float-right" > Sign in </button>;
  }


	render(){
      return(
        <Auxilary>
                <div className="container mb-8 mt-8 auth-form">
                    <div className="row">
                      <div className="col-md-4 offset-md-4">
                        { (this.state.errorMessage) ?
                          <div className="alert alert-danger" role="alert">
                              {this.state.errorMessage}
                          </div>
                          :null
                        }
                        <h2 className="mb-4 text-center"><b>Welcome! </b></h2>
                        <form onSubmit={(event) => this.loginDataHandler(event)}>

                          <div className="form-group">

                            <input
                              type='email'
                              className={(this.state.email!=='')?"form-control filled": "form-control"}
                              value={this.state.email}
                              onChange={(event)=>this.setState({email: event.target.value})}
                              onClick={(event)=>this.setState({errorMessage: null})}
                              required
                            />
                            <label>Email</label>                            
                          </div>

                          <div className="form-group">

                            <input
                                type='password'
                                className={(this.state.password!=='')?"form-control filled": "form-control"}
                                value={this.state.password}
                                onChange={(event)=>this.setState({password: event.target.value})}
                                onClick={(event)=>this.setState({errorMessage: null})}
                                required
                            />
                            <label>Password</label>
                          </div>

                          <div className="form-group forgot clearfix">
                              {this.renderButton()}
                          </div>


                        </form>
                      </div>                  
                    </div>
                </div>
        </Auxilary>
			);
	}
}


const mapStateToProps = state => {
	return{
    user: state.user
	}
};

export default connect(mapStateToProps, {loginUser})(Login);
